INSERT INTO `book` (`book_id`, `b_title`, `book_price`, `author`, `book_quantity`, `b_language_id`, `sherlf_date`, `revise_date`, `book_img_id`, `b_genre_id`, `blurb`, `book_status_id`, `b_type_id`, `isbn`) VALUES
(1, '一個人的懶人高纖低脂餐', 111, '安娜．席玲羅．漢普頓', 993, 1, '2023-07-04 00:00:00', '2023-07-17 00:00:00', '一個人的懶人高纖低脂餐7-1.png', 7, '很想好好為自己做頓飯，但一想到煮完還要收拾亂七八糟的廚房，就提不起勁？\r\n明知外食不健康，下班卻總累到沒力氣為自己準備營養美味的餐點？\r\n想要試著管理健康和體重，卻不太會料理？\r\n坊間的食譜看起來都好難，份量又好多？', 1, '2', 87),
(2, '守護大腦的飲食聖經', 299, 'Anthony William', 990, 1, '2023-07-02 00:00:00', '2023-07-17 00:00:00', '守護大腦的飲食聖經7-2.png', 7, '「許多人迫切希望恢復健康。然而，如果有人採取一般自然健康的方向，即依賴去除加工食品等基本措施，他們很容易陷入趨勢的陷阱，健康狀況不會有太大的起色，被迫嘗試一個又一個新潮的理論，充當誤導假設說的白老鼠。 如果是這樣，大腦需要很長的時間才能恢復—如果還有機會恢復的話。」— 安東尼．威廉', 1, '2', 87),
(3, '二○三○ 健保大限7-3', 199, '張鴻仁 ', 999, 1, '2023-07-02 00:00:00', '2023-07-09 00:00:00', '二○三○ 健保大限7-3.png', 7, '歷練藥政、防疫、健保、生技，無疫不與的張鴻仁  \r\n寫給兩千三百萬人的關鍵報告\r\n全面分析「世界第一」的台灣健保，榮光背後的坎坷與挑戰\r\n如果我們不認真的支持全民健保，等到2030年就來不及了！', 1, '2', 87),
(4, '走向雲端 病醫雙贏：健保改革日記', 99, '李伯璋', 996, 1, '2023-07-02 00:00:00', '2023-07-09 00:00:00', '走向雲端 病醫雙贏：健保改革日記7-4.png', 7, '醫療團隊必須合作才能有效率地解決患者病痛，就像手術房內的場景能充分展現有默契的醫療行為！此刻，落實分級醫療及向民眾推動社區厝邊好醫師的觀念，更是台灣醫療改革的里程碑。', 1, '2', 87),
(5, '第三波健保改革之路', 359, '黃煌雄...', 981, 1, '2023-07-16 00:00:00', '2023-07-23 00:00:00', '第三波健保改革之路7-5.png', 7, '透過多年來累積的全民健保資料庫，「口罩實名制」得以快速建立認證機制，旅遊史、病史等資訊的即時查詢與分享，也因「健保雲端系統」而得以實行，並且能夠即時掌握病患居住地區或工作場所是否有通報個案聚集，與健保資料勾稽，再回饋疫情指揮中心與醫療院所，協助醫療院所醫師與醫療人員及時判斷下一步該如何做，避免疫情進擴大或導致院內感染', 1, '2', 87),
(6, '怦然心動的人生整理魔法', 245, '近藤麻理惠', 992, 1, '2023-07-02 00:00:00', '2023-07-10 00:00:00', '怦然心動的人生整理魔法8-1.png', 8, '為什麼一本整理書，會讓無數人邊讀邊落淚？\r\n為什麼嬌小的麻理惠，一說起整理，竟光彩煥發得像巨人？為什麼一席難求的心動整理課，卻沒有學員回來再上第二次？\r\n翻開這本書，你不但會知道答案，\r\n你的人生也將因此煥然一新！', 1, '2', 87),
(7, '一休陪你一起愛瘦身：5個燃脂動作，甩肉25公斤，線條再升級！ （附一休示範大全集DVD+記錄了就會瘦三折海報）', 99, '李一休', 999, 1, '2023-07-03 00:00:00', '2023-07-18 00:00:00', '一休陪你一起愛瘦身8-2.png', 8, '你還在當減肥游牧者、到處找新瘦身法？\r\n從今天開始砍掉重練吧，就讓一休陪你一起愛瘦身！', 1, '2', 87),
(8, '代謝力UP減醣好好', 99, '娜塔', 990, 1, '2023-07-18 00:00:00', '2023-07-24 00:00:00', '代謝力UP減醣好好8-3.png', 8, '這本《代謝力UP減醣好好》具有清晰易懂的知識和容易執行的技巧，幫助初學者或已有減醣基礎的人更有效提升代謝力、更有動力持續執行。只要好好執行，絕對能讓你的減醣速度突飛猛進。', 1, '2', 87),
(9, '增肌減脂：4+2R代謝飲食法', 145, '王姿允, 急診女醫師其實.', 998, 1, '2023-07-02 00:00:00', '2023-07-10 00:00:00', '增肌減脂8-4.png', 8, '百位女醫親身實證，輕鬆月瘦11%體脂率\r\n不爆汗、不挨餓、不困難、無痛苦，零副作用、超滿足的超效減脂法', 1, '2', 87),
(10, '每週2天輕斷食，2個月瘦8公斤！：高醫減重班美女營養師的台灣味500卡菜單，在家吃、外食族都能瘦', 488, '宋侑璇', 996, 1, '2023-07-02 00:00:00', '2023-07-02 00:00:00', '每週2天輕斷食8-5.png', 8, '妳還在深信「不吃會瘦」、「聽說會瘦」的減肥方法？別傻了！\r\n真正能瘦身成功，關鍵在於「吃的技術」──\r\n週一、週四吃500卡，其餘5天正常吃，\r\n2個月就能瘦8公斤！', 1, '2', 87),
(11, 'JR PASS新幹線玩日本全攻略：7條旅遊路線＋7大分區導覽，從購買兌換到搭乘使用，從行程規畫到最新資訊，一票到底輕鬆遊全日本', 99, '劉盈慧', 999, 1, '2023-07-03 00:00:00', '2023-07-03 00:00:00', 'JR PASS新幹線玩日本全攻略9-1.png', 9, '新增疫後日本旅遊景點變化，更新JR PASS交通規則和各地景點資訊，資料最新最正確！', 1, '2', 87),
(12, '去日本自助旅行！給超新手的旅遊密技全圖解：交通攻略X食宿玩買X旅程規劃，有問必答萬用QA 暢銷最新版', 145, '超級旅行貓', 999, 1, '2023-07-19 00:00:00', '2023-07-28 00:00:00', '去日本自助旅行9-2.png', 9, '', 1, '2', 87),
(13, '我愛東京旅游失心瘋', 111, 'VERA', 999, 1, '2023-07-09 00:00:00', '2023-07-09 00:00:00', '我愛東京旅遊失心瘋9-3.png', 9, '', 1, '2', 87),
(14, '沒玩過這些地方，別再說你懂日本', 111, '熱血威爾, 熱血P, ARC 嵐, 毛弟 JJ, 月翔', 999, 1, '2023-07-03 00:00:00', '2023-07-04 00:00:00', '沒玩過這些地方，別再說你懂日本9-4.png', 9, '就是要去不一樣的地方！四位資深日本旅遊達人出陣！　超強四大主題旅行提案登場，超過70個壓箱景點大公開！', 1, '2', 87),
(15, '北海道自助這樣玩 交通×票券×行程規劃全指南，一看就懂的超實用旅遊攻略', 111, '超級旅行貓', 998, 1, '2023-07-23 00:00:00', '2023-07-17 00:00:00', '北海道自助這樣玩 交通×票券×行程規劃全指南9-5-1.png', 9, '', 1, '2', 87),
(16, '夜空中，昴星最美', 111, 'SEN-koi。製作委員會', 999, 1, '2023-07-02 00:00:00', '2023-07-09 00:00:00', '夜空中，昴星最美10-1.png', 10, '★日本學研出版社精心企劃，獲得全國的國、高中生一致好評！\r\n★浪漫少女漫畫 × 清新小說情節 × 有趣主題學習，新形式輕小說登場！', 1, '2', 87),
(17, '鈴芽之旅', 777, '新海誠', 999, 1, '2023-07-16 00:00:00', '2023-07-16 00:00:00', '鈴芽之旅10-2.png', 10, '牽繫著現在、過去，與未來，\r\n鈴芽的關門之旅　就此展開──', 1, '2', 87),
(18, '小書痴的下剋上FANBOOK(6)', 112, '香月美夜', 993, 1, '2023-07-16 00:00:00', '2023-07-23 00:00:00', '小書痴的下剋上FANBOOK(6)10-3.png', 10, '', 1, '2', 87),
